mapper.select:
SELECT (SELECT CD_NM FROM CD WHERE CD_VALUE = va.VCATN_TY_CD) AS VCATN_TY_CD ,(SELECT EMPNO FROM EMP WHERE EMP_ID IN (SELECT ATRZ_DMND_EMP_ID FROM ELCTRN_ATRZ ea WHERE ea.ELCTRN_ATRZ_ID = va.ELCTRN_ATRZ_ID ) ) as EMPNO ,(SELECT EMP_FLNM FROM EMP WHERE EMP_ID IN (SELECT ATRZ_DMND_EMP_ID FROM ELCTRN_ATRZ ea WHERE ea.ELCTRN_ATRZ_ID = va.ELCTRN_ATRZ_ID ) ) as EMP_FLNM , VCATN_BGNG_YMD , VCATN_END_YMD , VCATN_DE_CNT , VCATN_PRVONSH , COUNT(*) OVER () AS TOTAL_ITEMS FROM VCATN_ATRZ va WHERE 1=1
AND VCATN_BGNG_YMD > #{vcatnBgngYmd}
AND VCATN_END_YMD > #{vcatnEndYmd}
AND VCATN_BGNG_YMD BETWEEN #{vcatnBgngYmd} AND #{vcatnEndYmd}
HAVING EMPNO = #{empno}
