mapper.select:
SELECT pma.EMP_ID, d.DEPT_NM, e.EMP_FLNM, CONCAT(SUBSTR(#{startYmOdr},1, 6),'-',SUBSTR(#{startYmOdr}, 7, 1)) AS START_APLY_ODR, CONCAT(SUBSTR(#{endYmOdr},1, 6),'-',SUBSTR(#{endYmOdr}, 7, 1)) AS END_APLY_ODR, mm.EXPECT_MM, ( SELECT SUM(a.MD*8) / 88 FROM PRJCT_MM_APLY a WHERE a.PRJCT_ID = pma.PRJCT_ID GROUP BY a.PRJCT_ID ) AS TOTAL_MD_SUM, (mm.EXPECT_MM - ( SELECT SUM(a.MD*8) / 88 FROM PRJCT_MM_APLY a WHERE a.PRJCT_ID = pma.PRJCT_ID GROUP BY a.PRJCT_ID )) AS USE_MM, SUM(pma.MD*8) AS CLM_MD FROM PRJCT_MM_APLY pma JOIN PRJCT_INDVDL_CT_MM picm ON pma.PRJCT_ID = picm.PRJCT_ID AND pma.EMP_ID = picm.EMP_ID AND pma.APLY_YM = picm.APLY_YM AND pma.APLY_ODR = picm.APLY_ODR AND picm.MM_ATRZ_CMPTN_YN = 'Y' JOIN EMP e ON e.EMP_ID = picm.EMP_ID JOIN DEPT_HNF dh ON dh.EMP_ID = picm.EMP_ID JOIN DEPT d ON d.DEPT_ID = dh.DEPT_ID JOIN ( SELECT mlp.EMP_ID, mim.EXPECT_MM, mlp.PRJCT_ID, mim.INPT_YM FROM MMNY_LBRCO_PRMPC mlp JOIN MMNY_INPT_MM mim ON mlp.PRJCT_ID = mim.PRJCT_ID AND mlp.BGT_MNG_ODR = mim.BGT_MNG_ODR AND mlp.MMNY_LBRCO_PRMPC_SN = mim.MMNY_LBRCO_PRMPC_SN ) mm ON mm.PRJCT_ID = picm.PRJCT_ID AND mm.EMP_ID = picm.EMP_ID WHERE 1 = 1
AND pma.PRJCT_ID = #{prjctId}
AND e.EMP_FLNM = #{empFlnm}
AND CONCAT(pma.APLY_YM, pma.APLY_ODR) BETWEEN #{startYmOdr} AND #{endYmOdr}
AND pma.EMP_ID = #{empId}
GROUP BY pma.EMP_ID, d.DEPT_NM, e.EMP_FLNM, START_APLY_ODR, END_APLY_ODR, mm.EXPECT_MM, USE_MM

SELECT (SELECT CD_NM FROM nwTr.CD cd WHERE UP_CD_VALUE = 'VTW045' AND CD_VALUE = pca.EXPENS_CD) AS EXPENS_NM, pca.EXPENS_CD, CONCAT(SUBSTR(#{startYmOdr},1, 6),'-',SUBSTR(#{startYmOdr}, 7, 1)) AS START_APLY_ODR, CONCAT(SUBSTR(#{endYmOdr},1, 6),'-',SUBSTR(#{endYmOdr}, 7, 1)) AS END_APLY_ODR, ( SELECT SUM(a.UTZTN_AMT) FROM PRJCT_CT_APLY a WHERE a.EXPENS_CD = pca.EXPENS_CD AND a.PRJCT_ID = pca.PRJCT_ID GROUP BY a.EXPENS_CD ) AS TOTAL_AMT_SUM, SUM(pca.UTZTN_AMT) AS TOTAL_AMT FROM PRJCT_CT_APLY pca JOIN PRJCT_INDVDL_CT_MM picm ON pca.PRJCT_ID = picm.PRJCT_ID AND pca.EMP_ID = picm.EMP_ID AND pca.APLY_YM = picm.APLY_YM AND pca.APLY_ODR = picm.APLY_ODR AND picm.CT_ATRZ_CMPTN_YN = 'Y' WHERE 1 = 1
AND pca.PRJCT_ID = #{prjctId}
AND CONCAT(pca.APLY_YM, pca.APLY_ODR) BETWEEN #{startYmOdr} AND #{endYmOdr}
AND pca.EXPENS_CD = #{expensCd}
GROUP BY TOTAL_AMT_SUM, EXPENS_CD, START_APLY_ODR, END_APLY_ODR

SELECT pma.MD*8, CONCAT(pma.MD*8, 'hrs | ', e.EMP_FLNM, '(', c.CD_NM, ')') AS TEXT, c.CD_NM AS ATRZ_DMND_STTS_NM , DATE_FORMAT(pma.APLY_YMD, '%Y-%m-%d') AS START_DATE, DATE_FORMAT(pma.APLY_YMD,'%Y-%m-%d') AS END_DATE, (SELECT EMP_FLNM FROM EMP WHERE EMP_ID = pah.APRVR_EMP_ID) AS APRVR_EMP_FLNM, d.DEPT_NM FROM PRJCT_MM_APLY pma JOIN PRJCT_ATRZ_HIST pah ON pma.PRJCT_ID = pah.PRJCT_ID AND pma.EMP_ID = pah.EMP_ID AND pma.APLY_YM = pah.APLY_YM AND pma.APLY_ODR = pah.APLY_ODR AND pah.ATRZ_DMND_STTS_CD = 'VTW03703' -- 결재요청상태코드 : 승인 AND pah.PRJCT_APRVR_CD = 'VTW04701' -- 프로젝트결재자코드 : PM JOIN EMP e ON pma.EMP_ID = e.EMP_ID JOIN CD c ON pah.ATRZ_DMND_STTS_CD = c.CD_VALUE JOIN DEPT_HNF dh ON dh.EMP_ID = e.EMP_ID JOIN DEPT d ON d.DEPT_ID = dh.DEPT_ID WHERE 1 = 1
AND pma.PRJCT_ID = #{prjctId}
AND CONCAT(pma.APLY_YM, pma.APLY_ODR) BETWEEN #{startYmOdr} AND #{endYmOdr}
AND pma.EMP_ID = #{empId}

SELECT PIVOT_DATE, COALESCE(APLY_YMD, '') AS APLY_YMD, COALESCE(PRJCT_NM, 'null_project') AS PRJCT_NM, COALESCE(EMP_FLNM, 'null_flnm') AS EMP_FLNM, COALESCE(EMP_DETAIL, 'null_detail') AS EMP_DETAIL, COALESCE(CD_NM, 'null_cd') AS CD_NM, COALESCE(UTZTN_AMT, '') AS UTZTN_AMT FROM ( WITH RECURSIVE a AS ( SELECT CAST(CONCAT(SUBSTR(#{startYmOdr},1, 6), '01') AS DATE) AS PIVOT_DATE UNION ALL SELECT DATE_ADD(PIVOT_DATE, INTERVAL 1 DAY) FROM a WHERE DATE_ADD(PIVOT_DATE, INTERVAL 1 DAY) < CAST(CONCAT(SUBSTR(#{endYmOdr}, 1, 6), '01') AS DATE) + INTERVAL 1 MONTH ), b AS ( SELECT CONCAT(SUBSTR(b.UTZTN_DT, 1, 4),'-',SUBSTR(b.UTZTN_DT, 5, 2),'-',SUBSTR(b.UTZTN_DT, 7, 2)) AS APLY_YMD, p.PRJCT_NM, (SELECT EMP_FLNM FROM nwTr.EMP emp WHERE EMP_ID = b.EMP_ID) AS EMP_FLNM, CONCAT('상세내역 : ', b.CT_PRPOS, ' | 용도 : ', b.ATDRN) AS EMP_DETAIL, null AS MD, (SELECT CD_NM FROM nwTr.CD cd WHERE UP_CD_VALUE = 'VTW045' AND CD_VALUE = b.EXPENS_CD) AS CD_NM, b.UTZTN_AMT, b.USE_OFFIC, b.CT_PRPOS, b.ATDRN FROM nwTr.PRJCT_INDVDL_CT_MM a, nwTr.PRJCT_CT_APLY b JOIN PRJCT p ON b.PRJCT_ID = p.PRJCT_ID WHERE 1 = 1 AND a.PRJCT_ID = b.PRJCT_ID AND a.APLY_YM = b.APLY_YM AND a.APLY_ODR = b.APLY_ODR AND a.EMP_ID = b.EMP_ID AND a.CT_ATRZ_CMPTN_YN = 'Y'
AND a.PRJCT_ID = #{prjctId}
AND CONCAT(a.APLY_YM, a.APLY_ODR) BETWEEN #{startYmOdr} AND #{endYmOdr}
AND b.EXPENS_CD = #{expensCd}
) SELECT * FROM ( SELECT * FROM a WHERE ( (SUBSTR(#{startYmOdr},7, 1) = 1 AND PIVOT_DATE >= CONCAT(SUBSTR(#{startYmOdr},1, 6), '01')) OR (SUBSTR(#{startYmOdr},7, 1) = 2 AND PIVOT_DATE >= CONCAT(SUBSTR(#{startYmOdr},1, 6), '16')) ) AND ( (SUBSTR(#{endYmOdr},7, 1) = 1 AND PIVOT_DATE <= CONCAT(SUBSTR(#{endYmOdr},1, 6), '15')) OR (CONCAT(SUBSTR(#{endYmOdr}, 7, 1) = 2 AND PIVOT_DATE <= LAST_DAY(CAST(CONCAT(SUBSTR(#{endYmOdr}, 1, 6), '01') AS DATE)))) ) ) c LEFT JOIN b ON c.PIVOT_DATE = b.APLY_YMD ) a ORDER BY PIVOT_DATE

SELECT PIVOT_DATE, COALESCE(APLY_YMD, '') AS APLY_YMD, COALESCE(PRJCT_NM, 'null_project') AS PRJCT_NM, COALESCE(EMP_FLNM, 'null_flnm') AS EMP_FLNM, COALESCE(EMP_DETAIL, 'null_detail') AS EMP_DETAIL, COALESCE(MD, '') AS MD, COALESCE(CD_NM, 'null_cd') AS CD_NM, COALESCE(UTZTN_AMT, '') AS UTZTN_AMT, COALESCE(USE_OFFIC, '') AS USE_OFFIC, COALESCE(CT_PRPOS, '') AS CT_PRPOS, COALESCE(ATDRN, '') AS ATDRN FROM ( WITH RECURSIVE a AS ( SELECT CONCAT(SUBSTR(b.APLY_YMD, 1, 4),'-',SUBSTR(b.APLY_YMD, 5, 2),'-',SUBSTR(b.APLY_YMD, 7, 2)) AS APLY_YMD, p.PRJCT_NM, (SELECT EMP_FLNM FROM nwTr.EMP emp WHERE EMP_ID = b.EMP_ID) AS EMP_FLNM, '' AS EMP_DETAIL, FORMAT(b.MD*8, 1) AS MD, '근무 시간' AS CD_NM, null AS UTZTN_AMT, null AS USE_OFFIC, null AS CT_PRPOS, null AS ATDRN FROM nwTr.PRJCT_INDVDL_CT_MM a, nwTr.PRJCT_MM_APLY b JOIN PRJCT p ON b.PRJCT_ID = p.PRJCT_ID WHERE 1 = 1 AND a.PRJCT_ID = b.PRJCT_ID AND a.APLY_YM = b.APLY_YM AND a.APLY_ODR = b.APLY_ODR AND a.EMP_ID = b.EMP_ID AND a.MM_ATRZ_CMPTN_YN = 'Y'
AND a.PRJCT_ID = #{prjctId}
AND a.EMP_ID = #{empId}
AND CONCAT(a.APLY_YM, a.APLY_ODR) BETWEEN #{startYmOdr} AND #{endYmOdr}
), b AS ( SELECT CONCAT(SUBSTR(b.UTZTN_DT, 1, 4),'-',SUBSTR(b.UTZTN_DT, 5, 2),'-',SUBSTR(b.UTZTN_DT, 7, 2)) AS APLY_YMD, p.PRJCT_NM, (SELECT EMP_FLNM FROM nwTr.EMP emp WHERE EMP_ID = b.EMP_ID) AS EMP_FLNM, CONCAT('상세내역 : ', b.CT_PRPOS, ' | 용도 : ', b.ATDRN) AS EMP_DETAIL, null AS MD, (SELECT CD_NM FROM nwTr.CD cd WHERE UP_CD_VALUE = 'VTW045' AND CD_VALUE = b.EXPENS_CD) AS CD_NM, b.UTZTN_AMT, b.USE_OFFIC, b.CT_PRPOS, b.ATDRN FROM nwTr.PRJCT_INDVDL_CT_MM a, nwTr.PRJCT_CT_APLY b JOIN PRJCT p ON b.PRJCT_ID = p.PRJCT_ID WHERE 1 = 1 AND a.PRJCT_ID = b.PRJCT_ID AND a.APLY_YM = b.APLY_YM AND a.APLY_ODR = b.APLY_ODR AND a.EMP_ID = b.EMP_ID AND a.MM_ATRZ_CMPTN_YN = 'Y'
AND a.PRJCT_ID = #{prjctId}
AND a.EMP_ID = #{empId}
AND CONCAT(a.APLY_YM, a.APLY_ODR) BETWEEN #{startYmOdr} AND #{endYmOdr}
), c AS ( SELECT CAST(CONCAT(SUBSTR(#{startYmOdr},1, 6), '01') AS DATE) AS PIVOT_DATE UNION ALL SELECT DATE_ADD(PIVOT_DATE, INTERVAL 1 DAY) FROM c WHERE DATE_ADD(PIVOT_DATE, INTERVAL 1 DAY) < CAST(CONCAT(SUBSTR(#{endYmOdr}, 1, 6), '01') AS DATE) + INTERVAL 1 MONTH ) SELECT * FROM ( SELECT * FROM c WHERE ( (SUBSTR(#{startYmOdr},7, 1) = 1 AND PIVOT_DATE >= CONCAT(SUBSTR(#{startYmOdr},1, 6), '01')) OR (SUBSTR(#{startYmOdr},7, 1) = 2 AND PIVOT_DATE >= CONCAT(SUBSTR(#{startYmOdr},1, 6), '16')) ) AND ( (SUBSTR(#{endYmOdr},7, 1) = 1 AND PIVOT_DATE <= CONCAT(SUBSTR(#{endYmOdr},1, 6), '15')) OR (CONCAT(SUBSTR(#{endYmOdr}, 7, 1) = 2 AND PIVOT_DATE <= LAST_DAY(CAST(CONCAT(SUBSTR(#{endYmOdr}, 1, 6), '01') AS DATE)))) ) ) d LEFT JOIN a ON d.PIVOT_DATE = a.APLY_YMD UNION SELECT * FROM ( SELECT * FROM c WHERE ( (SUBSTR(#{startYmOdr},7, 1) = 1 AND PIVOT_DATE >= CONCAT(SUBSTR(#{startYmOdr},1, 6), '01')) OR (SUBSTR(#{startYmOdr},7, 1) = 2 AND PIVOT_DATE >= CONCAT(SUBSTR(#{startYmOdr},1, 6), '16')) ) AND ( (SUBSTR(#{endYmOdr},7, 1) = 1 AND PIVOT_DATE <= CONCAT(SUBSTR(#{endYmOdr},1, 6), '15')) OR (CONCAT(SUBSTR(#{endYmOdr}, 7, 1) = 2 AND PIVOT_DATE <= LAST_DAY(CAST(CONCAT(SUBSTR(#{endYmOdr}, 1, 6), '01') AS DATE)))) ) ) d LEFT JOIN b ON d.PIVOT_DATE = b.APLY_YMD ) a ORDER BY PIVOT_DATE

SELECT P.PRJCT_ID AS PRJCT_ID, DATE_FORMAT(PMA.APLY_YMD , '%Y-%m-%d') AS APLY_YMD, P.PRJCT_NM AS PRJCT_NM, E.EMP_FLNM AS EMP_FLNM, CASE WHEN PMA.MD = 1 THEN 8 WHEN PMA.MD = 0.5 THEN 4 ELSE 0 END AS MD FROM PRJCT_MM_APLY PMA, PRJCT P , PRJCT_INDVDL_CT_MM PICM, EMP E WHERE 1=1 AND PMA.PRJCT_ID = PICM.PRJCT_ID AND PMA.EMP_ID = PICM.EMP_ID AND PMA.APLY_YM = PICM.APLY_YM AND PMA.APLY_ODR = PICM.APLY_ODR AND P.PRJCT_ID = PMA.PRJCT_ID AND PMA.EMP_ID = E.EMP_ID AND PICM.MM_ATRZ_CMPTN_YN = 'Y'
AND DATE_FORMAT(PMA.APLY_YMD , '%Y') = #{yearItem}
AND DATE_FORMAT(PMA.APLY_YMD , '%m') = #{monthItem}
AND PMA.APLY_ODR = #{aplyOdr}
AND PMA.PRJCT_ID = #{prjctId}

SELECT PIVOT_DATE, COALESCE(b.PRJCT_NM, 'null_project') AS PRJCT_NM, COALESCE(b.EXPENS_CD, 'null_Cd') AS EXPENS_CD, COALESCE(b.EMP_FLNM, 'null_flnm') AS EMP_FLNM, COALESCE(b.PRJCT_DETAIL, 'null_info') AS PRJCT_DETAIL, COALESCE(b.EMP_DETAIL, 'null_info') AS EMP_DETAIL, COALESCE(b.UTZTN_AMT, 'null_amt') AS UTZTN_AMT, COALESCE(b.UTZTN_DT, 'null_dt') AS UTZTN_DT FROM ( WITH RECURSIVE a AS ( SELECT CAST(CONCAT(#{aplyYm}, '01') AS DATE) AS PIVOT_DATE UNION ALL SELECT DATE_ADD(PIVOT_DATE, INTERVAL 1 DAY) FROM a WHERE DATE_ADD(PIVOT_DATE, INTERVAL 1 DAY) < CAST(CONCAT(#{aplyYm}, '01') AS DATE) + INTERVAL 1 MONTH ) SELECT * FROM a
WHERE ( #{aplyOdr} = 1 AND DAY(PIVOT_DATE) <= 15 ) OR ( #{aplyOdr} = 2 AND DAY(PIVOT_DATE) > 15 AND DAY(PIVOT_DATE) <= DAY(LAST_DAY(PIVOT_DATE)))
) a LEFT JOIN ( SELECT CONCAT(p.PRJCT_NM, ' (PM : ', (SELECT EMP_FLNM FROM EMP WHERE EMP_ID = pah.APRVR_EMP_ID), ')') AS PRJCT_NM, (SELECT CD_NM FROM CD WHERE CD_VALUE = pca.EXPENS_CD) AS EXPENS_CD, e.EMP_FLNM, CONCAT(e.EMP_FLNM, ' | 상세내역 : ', pca.CT_PRPOS, ' | 용도 : ', pca.ATDRN) AS PRJCT_DETAIL, CONCAT('상세내역 : ', pca.CT_PRPOS, ' | 용도 : ', pca.ATDRN) AS EMP_DETAIL, pca.UTZTN_AMT, CONCAT(SUBSTR(pca.UTZTN_DT, 1, 4),'-',SUBSTR(pca.UTZTN_DT, 5, 2),'-',SUBSTR(pca.UTZTN_DT, 7, 2)) AS UTZTN_DT FROM PRJCT_CT_APLY pca JOIN PRJCT_ATRZ_HIST pah ON pca.PRJCT_ID = pah.PRJCT_ID AND pca.EMP_ID = pah.EMP_ID AND pca.APLY_YM = pah.APLY_YM AND pca.APLY_ODR = pah.APLY_ODR AND pah.ATRZ_DMND_STTS_CD = 'VTW03703' -- 결재요청상태코드 : 승인 AND pah.PRJCT_APRVR_CD = 'VTW04701' -- 프로젝트결재자코드 : PM JOIN EMP e ON pca.EMP_ID = e.EMP_ID JOIN CD c ON pah.ATRZ_DMND_STTS_CD = c.CD_VALUE JOIN PRJCT p ON pca.PRJCT_ID = p.PRJCT_ID WHERE 1 = 1
AND pca.PRJCT_ID = #{prjctId}
AND pca.APLY_YM = #{aplyYm}
AND pca.APLY_ODR = #{aplyOdr}
AND e.EMPNO = #{empNo}
AND pca.EXPENS_CD = #{expensCd}
) b ON a.PIVOT_DATE = b.UTZTN_DT ORDER BY PIVOT_DATE

SELECT
W_EMP_ID AS EMP_ID, EMP_FLNM , PRJCT_NM, FORMAT(PRJCT_NON_INSERT, 1) AS PRJCT_NON_INSERT, FORMAT(PRJCT_INSERT, 1) AS PRJCT_INSERT, FORMAT(VACATION, 1) AS VACATION, FORMAT(TOTAL_PRJCT_NON_INSERT, 1) AS TOTAL_PRJCT_NON_INSERT, FORMAT(TOTAL_PRJCT_INSERT, 1) AS TOTAL_PRJCT_INSERT, PRJCT_NON_INSERT_EXPENSE, PRJCT_INSERT_EXPENSE AS PRJCT_INSERT_EXPENSE FROM ( SELECT E.EMP_ID AS W_EMP_ID, E.EMP_FLNM AS EMP_FLNM, P.PRJCT_NM AS PRJCT_NM , P.PRJCT_ID AS W_PRJCT_ID, SUM(CASE WHEN P.PRJCT_STLE_CD = 'VTW01801' THEN CASE WHEN PMA.MD = 0 THEN 0 WHEN PMA.MD = 0.5 THEN 4 WHEN PMA.MD = 1 THEN 8 ELSE 0 END ELSE 0 END) AS PRJCT_NON_INSERT, SUM(CASE WHEN P.PRJCT_STLE_CD = 'VTW01802' THEN CASE WHEN PMA.MD = 0 THEN 0 WHEN PMA.MD = 0.5 THEN 4 WHEN PMA.MD = 1 THEN 8 ELSE 0 END ELSE 0 END) AS PRJCT_INSERT, SUM(CASE WHEN PMA.YRYC_RATE = 0 THEN 0 WHEN PMA.YRYC_RATE = 0.5 THEN 4 WHEN PMA.YRYC_RATE = 1 THEN 8 ELSE 0 END) AS VACATION , (SUM(CASE WHEN P.PRJCT_STLE_CD = 'VTW01801' THEN CASE WHEN PMA.MD = 0 THEN 0 WHEN PMA.MD = 0.5 THEN 4 WHEN PMA.MD = 1 THEN 8 ELSE 0 END ELSE 0 END) + SUM(CASE WHEN P.PRJCT_STLE_CD = 'VTW01801' THEN CASE WHEN PMA.YRYC_RATE = 0 THEN 0 WHEN PMA.YRYC_RATE = 0.5 THEN 4 WHEN PMA.YRYC_RATE = 1 THEN 8 ELSE 0 END ELSE 0 END) ) AS TOTAL_PRJCT_NON_INSERT, (SUM(CASE WHEN P.PRJCT_STLE_CD = 'VTW01802' THEN CASE WHEN PMA.MD = 0 THEN 0 WHEN PMA.MD = 0.5 THEN 4 WHEN PMA.MD = 1 THEN 8 ELSE 0 END ELSE 0 END) + SUM(CASE WHEN P.PRJCT_STLE_CD = 'VTW01802' THEN CASE WHEN PMA.YRYC_RATE = 0 THEN 0 WHEN PMA.YRYC_RATE = 0.5 THEN 4 WHEN PMA.YRYC_RATE = 1 THEN 8 ELSE 0 END ELSE 0 END) ) AS TOTAL_PRJCT_INSERT FROM PRJCT_INDVDL_CT_MM PICM , PRJCT_MM_APLY PMA, PRJCT P, EMP E WHERE PICM.PRJCT_ID = PMA.PRJCT_ID AND PICM.EMP_ID = PMA.EMP_ID AND PICM.APLY_YM = PMA.APLY_YM AND PICM.APLY_ODR = PMA.APLY_ODR AND P.PRJCT_ID = PICM.PRJCT_ID AND PMA.EMP_ID = E.EMP_ID AND PICM.MM_ATRZ_CMPTN_YN = 'Y' GROUP BY E.EMP_ID, E.EMP_FLNM , P.PRJCT_NM , P.PRJCT_ID, PMA.YRYC_RATE ) WORK_HOUR, ( SELECT E.EMP_ID AS C_EMP_ID, P.PRJCT_ID AS C_PRJCT_ID, P.PRJCT_NM AS C_PRJCT_NM, SUM(CASE WHEN P.PRJCT_STLE_CD = 'VTW01801' THEN PCA.UTZTN_AMT ELSE 0 END ) PRJCT_NON_INSERT_EXPENSE , SUM(CASE WHEN P.PRJCT_STLE_CD = 'VTW01802' THEN PCA.UTZTN_AMT ELSE 0 END ) PRJCT_INSERT_EXPENSE FROM PRJCT_INDVDL_CT_MM PICM , PRJCT P, EMP E, PRJCT_CT_APLY PCA WHERE 1 = 1 AND E.EMP_ID = PICM.EMP_ID AND PICM.PRJCT_ID = P.PRJCT_ID AND PICM.PRJCT_ID = PCA.PRJCT_ID AND PICM.EMP_ID = PCA.EMP_ID AND PICM.APLY_YM = PCA.APLY_YM AND PICM.APLY_ODR = PCA.APLY_ODR AND PICM.CT_ATRZ_CMPTN_YN = 'Y' GROUP BY E.EMP_ID, E.EMP_FLNM , P.PRJCT_NM, P.PRJCT_ID) CT_LIST WHERE WORK_HOUR.W_PRJCT_ID = CT_LIST.C_PRJCT_ID AND WORK_HOUR.W_EMP_ID = CT_LIST.C_EMP_ID
AND W_PRJCT_ID = #{prjctId}
