mapper.select:
SELECT ROW_NUMBER()OVER ()ROWNUM , DEPT_ID , DEPT_NM -- 부서명 , UP_DEPT_ID , (SELECT d2.DEPT_NM FROM DEPT d2 WHERE d.UP_DEPT_ID = d2.DEPT_ID) AS UP_DEPT_NM -- 상위부서명 , (SELECT EMP_FLNM FROM (SELECT e.EMP_FLNM , ROW_NUMBER() OVER (ORDER BY h.JBTTL_CD) AS RN FROM EMP e INNER JOIN DEPT_HNF h ON e.EMP_ID = h.EMP_ID WHERE h.DEPT_ID = d.DEPT_ID) a WHERE RN = 1) AS DEPT_MNGR_EMP_FLNM -- 부서장명 , DEPT_BGNG_YMD -- 부서시작일자 , DEPT_END_YMD -- 부서종료일자 , COUNT(*) OVER () AS TOTAL_ITEMS -- 총건수 FROM DEPT d WHERE 1=1
AND DEPT_NM LIKE CONCAT('%',#{deptNm},'%')
AND (SELECT EMP_FLNM FROM (SELECT e.EMP_FLNM , ROW_NUMBER() OVER (ORDER BY h.JBTTL_CD) AS RN FROM EMP e INNER JOIN DEPT_HNF h ON e.EMP_ID = h.EMP_ID WHERE h.DEPT_ID = d.DEPT_ID) a WHERE RN = 1 ) LIKE CONCAT('%',#{deptMngrEmpFlnm},'%')
AND DEPT_BGNG_YMD > #{deptBgngYmd}
AND DEPT_END_YMD > #{deptEndYmd}
AND DEPT_BGNG_YMD BETWEEN #{deptBgngYmd} AND #{deptEndYmd}
ORDER BY REG_DT DESC

SELECT (SELECT CD_NM FROM CD WHERE CD_VALUE = va.VCATN_TY_CD) AS VCATN_TY_CD ,(SELECT EMPNO FROM EMP WHERE EMP_ID IN (SELECT ATRZ_DMND_EMP_ID FROM ELCTRN_ATRZ ea WHERE ea.ELCTRN_ATRZ_ID = va.ELCTRN_ATRZ_ID ) ) as EMPNO ,(SELECT EMP_FLNM FROM EMP WHERE EMP_ID IN (SELECT ATRZ_DMND_EMP_ID FROM ELCTRN_ATRZ ea WHERE ea.ELCTRN_ATRZ_ID = va.ELCTRN_ATRZ_ID ) ) as EMP_FLNM , VCATN_BGNG_YMD , VCATN_END_YMD , VCATN_DE_CNT , VCATN_PRVONSH , COUNT(*) OVER () AS TOTAL_ITEMS FROM VCATN_ATRZ va WHERE 1=1
AND VCATN_BGNG_YMD > #{vcatnBgngYmd}
AND VCATN_END_YMD > #{vcatnEndYmd}
AND VCATN_BGNG_YMD BETWEEN #{vcatnBgngYmd} AND #{vcatnEndYmd}
HAVING EMPNO = #{empno}
